import React, { useState } from 'react';
import { Navbar, Container, Nav } from 'react-bootstrap';
import { Button } from "react-bootstrap";
import { useNavigate } from "react-router";
import { useUserAuth } from "../context/UserAuthContext";
import axios from "axios";
import "./Home.css";

const Home = () => {
  const { logOut, user } = useUserAuth();
  const navigate = useNavigate();
  const [showSidebar, setShowSidebar] = useState(true);
  const [inputText, setInputText] = useState("");
  const [generatedText, setGeneratedText] = useState("");

  const handleLogout = async () => {
    try {
      await logOut();
      navigate("/");
    } catch (error) {
      console.log(error.message);
    }
  };

  
  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };
    
  const handleGenerateText = async () => {
    try {
      const response = await axios({
        method: "post",
        url: `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=AIzaSyBJZ3bntauZYDFyqxCPH-Om16ctymz_gwg`,
        data: {
          prompt: inputText,
        },
        headers: {
          'Content-Type': 'application/json',
        },
      });
      setGeneratedText(response.data.generatedContent);
    } catch (error) {
      console.error("Error generating text:", error);
    }
  };


  return (
    <div className="d-flex">
      <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <Navbar.Brand href="#home">Cod EX</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="/signup">Sign Up</Nav.Link>
            <Nav.Link href="/">Log In</Nav.Link>
            <Nav.Link href="/home">Home</Nav.Link>
          </Nav>
          <Nav>
            <Button variant="primary" onClick={handleLogout}>
              Log out
            </Button>
            <Button variant="secondary" onClick={toggleSidebar}>
              {showSidebar ? '<<' : '>>'}
            </Button>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
      <div className="content flex-grow-1 p-4">
        <div className="p-4 box mt-3 text-center">
          <h1 style={{ fontSize: "32px" }}>Welcome</h1>
          <p style={{ fontSize: "18px" }}>{user && user.email}</p>
        </div>
        <div className="mt-4">
          <input
            type="text"
            value={inputText}
            onChange={handleInputChange}
            placeholder="Enter text to generate"
            className="form-control mb-2"
          />
          <Button variant="primary" onClick={handleGenerateText}>
            Generate Text
          </Button>
        </div>
        {generatedText && (
          <div className="mt-4 p-4 box">
            <h3>Generated Text:</h3>
            <p>{generatedText}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
