import React, { useState } from "react";
import { Button,Spinner } from "react-bootstrap";
import { useNavigate } from "react-router";
import { useUserAuth } from "../context/UserAuthContext";
import axios from "axios";
import "./Home.css";

const Home = () => {
  const { logOut, user } = useUserAuth();
  const navigate = useNavigate();
  const [showSidebar, setShowSidebar] = useState(true);
  const [inputText, setInputText] = useState("");
  const [generatedText, setGeneratedText] = useState("");
  const [isLoading, setIsLoading] = useState(false); 

  const handleLogout = async () => {
    try {
      await logOut();
      navigate("/");
    } catch (error) {
      console.log(error.message);
    }
  };

  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleGenerateText = async () => {
    setIsLoading(true);
  
    // Ensure inputText is not empty
    if (!inputText.trim()) {
      console.error("Input text is empty");
      setIsLoading(false);
      return;
    }
  
    try {
      const response = await axios.post(
        `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${process.env.REACT_APP_API_KEY}`,
        { prompt: inputText },
        { headers: { 'Content-Type': 'application/json' } }
      );
  
      console.log("Full API response:", response);
  
      if (response.data && response.data.candidates) {
        const generatedContent = response.data.candidates[0].content.parts[0].text;
        setGeneratedText(generatedContent);
        console.log("Generated text:", generatedContent);
      } else {
        console.error("Unexpected API response structure:", response.data);
      }
    } catch (error) {
      console.error("Error generating text:", error);
      if (error.response) {
        console.error("Error response:", error.response.data);
      } else if (error.request) {
        console.error("Error request:", error.request);
      } else {
        console.error("General error message:", error.message);
      }
      console.error("Error details:", error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="d-flex">
      <div className={`sidebar ${showSidebar ? "show" : "hide"}`}>
        <nav>
          <ul>
            <li><a href="home" style={{ fontSize: "24px" }}>EXPL<span style={{ fontWeight: "bold" }}>AI</span>NIFY</a></li>
            <li><a href="signup">Sign Up </a></li>
            <li><a href="/">Log In</a></li>
            <li><a href="home">Home</a></li>
            <li><Button variant="primary" onClick={handleLogout}>
              Log out
            </Button></li>
          </ul>
        </nav>
      </div>
      <Button variant="secondary" onClick={toggleSidebar} className="toggle-btn">
        {showSidebar ? "<<" : ">>"}
      </Button>
      <div className="content flex-grow-1 p-4">
        <div className="p-4 box mt-3 text-center">
          <h1 style={{ fontSize: "32px" }}>Welcome</h1>
          <p style={{ fontSize: "18px" }}>{user && user.email}</p>
        </div>
        <div className="mt-4">
          <input
            type="text"
            value={inputText}
            onChange={handleInputChange}
            placeholder="Enter text to generate"
            className="form-control mb-2"
          />
          <Button variant="primary" onClick={handleGenerateText} disabled={isLoading}>
        {isLoading ? <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" /> : "Generate Text"}
      </Button>
        </div>
        {generatedText && (
          <div className="mt-4 p-4 box">
            <h3>Generated Text:</h3>
            <p>{generatedText}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
