import React, { useState } from "react";
import { Button,Spinner } from "react-bootstrap";
import { useNavigate } from "react-router";
import { useUserAuth } from "../context/UserAuthContext";
import axios from "axios";
import "./Home.css";

const Home = () => {
  const { logOut, user } = useUserAuth();
  const navigate = useNavigate();
  const [showSidebar, setShowSidebar] = useState(true);
  const [inputText, setInputText] = useState("");
  const [generatedText, setGeneratedText] = useState("");
  const [isLoading, setIsLoading] = useState(false); 
  const [answer, setAnswer] = useState("");
  const [generatingAnswer, setGeneratingAnswer] = useState(false);


  const handleLogout = async () => {
    try {
      await logOut();
      navigate("/");
    } catch (error) {
      console.log(error.message);
    }
  };

  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleGenerateText = async () => {
    setIsLoading(true);

    setGeneratingAnswer(true);
    setAnswer("Loading your answer... \n It might take upto 10 seconds");

    if (!inputText.trim()) {
      console.error("Input text is empty");
      setIsLoading(false);
      return;
    }
  
    try {
      const response = await axios({
        url:  `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${process.env.REACT_APP_API_KEY}`,
        method: "post",
        data: {
          contents: [{ parts: [{ text: inputText }] }],
        },
      });
      setAnswer(
        response["data"]["candidates"][0]["content"]["parts"][0]["text"]
      );
    } catch (error) {
      console.log(error);
      setAnswer("Sorry - Something went wrong. Please try again!");
    }
    setGeneratingAnswer(false);
  };
  

  return (
    <div className="d-flex">
      <div className={`sidebar ${showSidebar ? "show" : "hide"}`}>
        <nav>
          <ul>
            <li><a href="home" style={{ fontSize: "24px" }}>EXPL<span style={{ fontWeight: "bold" }}>AI</span>NIFY</a></li>
            <li><a href="signup">Sign Up </a></li>
            <li><a href="/">Log In</a></li>
            <li><a href="home">Home</a></li>
            <li><Button variant="primary" onClick={handleLogout}>
              Log out
            </Button></li>
          </ul>
        </nav>
      </div>
      <Button variant="secondary" onClick={toggleSidebar} className="toggle-btn">
        {showSidebar ? "<<" : ">>"}
      </Button>
     
        <div className="mt-4">
          <input
            type="text"
            value={inputText}
            onChange={handleInputChange}
            placeholder="Enter text to generate"
            className="form-control mb-2"
          />
          <Button variant="primary" onClick={handleGenerateText} disabled={isLoading}>
        {isLoading ? <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" /> : "Generate Text"}
      </Button>
        {<div className="w-full md:w-2/3 lg:w-1/2 xl:w-1/3 text-center rounded-lg bg-white my-4 shadow-lg transition-all duration-500 transform hover:scale-105">
      <ReactMarkdown className="p-4">{answer}</ReactMarkdown>
    </div> && (
          <div className="mt-4 p-4 box">
            <h3>Generated Text:</h3>
            <p>{answer}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
