import React, { useState } from "react";
import { Button, Spinner } from "react-bootstrap";
import { useNavigate } from "react-router";
import { useUserAuth } from "../context/UserAuthContext";
import axios from "axios";
import "./Home.css";

const Home = () => {
  const { logOut } = useUserAuth();
  const navigate = useNavigate();
  const [showSidebar, setShowSidebar] = useState(true);
  const [inputText, setInputText] = useState("");
  const [isLoading, setIsLoading] = useState(false); 
  const [answer, setAnswer] = useState("");
  const [, setGeneratingAnswer] = useState(false);

  const handleLogout = async () => {
    try {
      await logOut();
      navigate("/");
    } catch (error) {
      console.log(error.message);
    }
  };

  const toggleSidebar = () => {
    setShowSidebar(!showSidebar);
  };

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleGenerateText = async () => {
    setIsLoading(true);
    setGeneratingAnswer(true);
    setAnswer("Loading your answer... \n It might take up to 10 seconds");

    if (!inputText.trim()) {
      console.error("Input text is empty");
      setIsLoading(false);
      setGeneratingAnswer(false);
      return;
    }

    try {
      const response = await axios({
        url: `https://generativelanguage.googleapis.com/v1beta/models/gemini-1.5-flash-latest:generateContent?key=${process.env.REACT_APP_API_KEY}`,
        method: "post",
        data: {
          contents: [{ parts: [{ text: inputText }] }],
        },
      });
      setAnswer(response.data.candidates[0].content.parts[0].text);
    } catch (error) {
      console.log(error);
      setAnswer("Sorry - Something went wrong. Please try again!");
    } finally {
      setIsLoading(false);
      setGeneratingAnswer(false);
    }
  };

  return (
    <>
      <h1 className="ttl mt-4">AI Code Explainer App</h1>
      <div className="d-flex">
        
        <div className="mt-4">
          <input
            type="text"
            value={inputText}
            onChange={handleInputChange}
            placeholder="Enter text to generate"
            className="form-control mb-2"
          />
          <Button variant="primary" onClick={handleGenerateText} disabled={isLoading}>
            {isLoading ? <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" /> : "Generate Text"}
          </Button>
          {answer && (
            <div className="w-full md:w-2/3 lg:w-1/2 xl:w-1/3 text-center rounded-lg bg-white my-4 shadow-lg transition-all duration-500 transform hover:scale-105">
              <h3>Generated Text:</h3>
              <p>{answer}</p>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default Home;
